mapper.select:
SELECT cateno, categrpno, name, seqno, visible, rdate, cnt
FROM cate
WHERE cateno=#{cateno}

mapper.update:
UPDATE cate
SET categrpno=#{categrpno}, name=#{name}, seqno=#{seqno}, visible=#{visible}, cnt=#{cnt}
WHERE cateno = #{cateno}

UPDATE cate
SET seqno = seqno - 1
WHERE cateno=#{cateno}

UPDATE cate
SET seqno = seqno + 1
WHERE cateno=#{cateno}

UPDATE cate
SET visible=#{visible}
WHERE cateno=#{cateno}

UPDATE cate 
SET cnt = cnt + 1 
WHERE cateno=#{cateno}

UPDATE cate 
SET cnt = cnt - 1 
WHERE cateno=#{cateno}
